 //form of author name
 //add new author 
 //delete author 
 //edit author 
 import { useEffect, useState } from "react";

 import axios from 'axios';
// import author from "../../../BackEnd/controllers/author";




export default function Book() {

    const [author,setAuthor]=useState();




    ///////////////////////
    //add author ****************

    useEffect(()=>{

        axios.get('http://localhost:3030/authors')
        .then((res)=>{
            console.log(res.data)
            setAuthor(res.data)
        })

    },[])





    return (  
        <>
         <div className="authorBox">

             {author.map((get)=>{

                 return <div className="authorCard">

                    <h3>{get.name}</h3>
                    <h3>{get.age}</h3>
                    <h3>{get.nationality}</h3>
                    <h3>{get.image}</h3>
                    <h3>{get.gender}</h3>

                    {get.books.map((item)=>{
           
           return <div>
               <h4>{item.title}</h4>
               <h4>{item.pages}</h4>
               <h4></h4>

           </div>

                    })}


                 </div>
             })}


         </div>
        </>
    );
}
