import react from 'react';
import { useEffect, useState } from "react";
import axios from 'axios'
import './book.css'



export default function Book() {

    //all book hear.
    const [book ,setBook] = useState([])

        const [Title,setTitle]=useState()
        const [Pages,setPages]=useState()
        const [Price,setPrice]=useState()
        const [Image ,setImage] =useState()


// ///////////////////////////////////
//display all data from database
    useEffect(()=>{ 
    
    axios.get('http://localhost:3030/books')
    .then((res)=>{
        console.log(res.data)
        setBook(res.data)
    })
    
    },[])
    // //////////////////////////////////
//add new info about  book
    const handelAdd=()=>{ 

        alert ('added new book')

        axios.post('http://localhost:3030/books/create',{title:Title,pages:Pages,price:Price})
       
        .then((res)=>{
            console.log('new book...')
            console.log(res.data)
            setBook(res.data)
        })

    }
     

    return (  
        <>
        <h2>Book file</h2>

<from  >


<div className="bookForm"> 

<label>Title:</label>
<input type="text"
 placeholder="Enter name of title .."
 onChange={e=>setTitle(e.target.value)}/>
<br>
</br>

<label>Pages:</label>
<input type="text"
 placeholder="Enter number of pages .."
 onChange={e=>setPages(e.target.value)}/>
<br>
</br>

<label>Price:</label>
<input type="text"
 placeholder="Enter the price .."
 onChange={e=>setPrice(e.target.value)}/>
<br>


</br>

 <button onClick={handelAdd}> Add</button>
</div>
</from>



        <div className="bookBox">

{/* display all data from database */}
        {book.map((get)=>{
            return <div className="bookCard">

   <img src={get.image} alt='' width={250} />

      <h2> <span>Title:</span>  {get.title}</h2>
      <h3><span>Pages:</span>   {get.pages}</h3>
      <h4> <span>Price:</span>  {get.price}$</h4>
 


            </div>
})}

        </div>
        </>
    );
}
