import react from 'react';
import { useEffect, useState } from "react";
import axios from 'axios'
import './book.css'
export default function Book() {

    const [book ,setBook] = useState([])


    useEffect(()=>{ 
    
    axios.get('http://localhost:3030/books')
    .then((res)=>{
        console.log(res.data)
        setBook(res.data)
    })
    
    },[])
    
     

    return (  
        <>
        <h2>Book file</h2>

<from className="bookForm">


<div></div>
<label>Title:</label>
<input type="text" placeholder="Enter name of title .."/>
<br>
</br>

<label>Pages:</label>
<input type="text" placeholder="Enter number of pages .."/>
<br>
</br>

<label>Price:</label>
<input type="text" placeholder="Enter the price .."/>
<br>
</br>

<input type="submit" />

</from>



        <div className="bookBox">

{/* display all data from database */}
        {book.map((get)=>{
            return <div className="bookCard">

   <img src={get.image} alt='' width={250} />

      <h2> <span>Title:</span>  {get.title}</h2>
      <h3><span>Pages:</span>   {get.pages}</h3>
      <h4> <span>Price:</span>  {get.price}$</h4>
 


            </div>
})}

        </div>
        </>
    );
}
