 import React, { useEffect ,useState} from "react";
import { useParams,useNavigate } from "react-router-dom";

import swal from 'sweetalert';
import axios from "axios";



export default function AuthorFullCard({data}) {

    const {_id} =useParams();
     const [newAuthor,setNewAuthor]= useState({});
    const [newBook,setNewBook]= useState({});
    const navigate=useNavigate()

////////////////////////////////////////////////////////////////
    const [Image ,setImage] =useState()
    const [Title,setTitle]=useState()
    const [Pages,setPages]=useState()
    const [Price,setPrice]=useState()

    useEffect(()=>{
        console.log(_id)
    })

    ////////////////////////////////////////////////////////////////
    const handelDelete=(id)=>{ 

      swal({
          title:'Author is deleted ',
          icon:'success'
        })
              console.log(id)
  
      axios.delete(`http://localhost:3030/authors/${id}/delete`)
      .then((res)=>{
          
          setNewAuthor(res.data)
      })
      navigate('/components/Author');
          }


   //add new info about  book
   const handelAdd=(e)=>{ 
e.preventDefault()
    swal({
        title:'Added New Book',
        icon:'success'
      })

    axios.post(`http://localhost:3030/books/book/${_id}`, 
    {image:Image, title:Title, pages:Pages, price:Price }
    )
    
    .then((res)=>{

        console.log(res.data)
        setNewBook(res.data)
    })
}



    return (
        <>
        {/* ............. */}

        <from >


<div className="bookForm"> 
<h2> Add new Book</h2>
<label>Image:</label>
<input type="text"
placeholder="Enter Cover of book .."
onChange={e=>setImage(e.target.value)}/>
<br>
</br>

<label>Title:</label>
<input type="text"
placeholder="Enter name of title .."
onChange={e=>setTitle(e.target.value)}/>
<br>
</br>

<label>Pages:</label>
<input type="text"
placeholder="Enter number of pages .."
onChange={e=>setPages(e.target.value)}/>
<br>
</br>

<label>Price:</label>
<input type="text"
placeholder="Enter the price .."
onChange={e=>setPrice(e.target.value)}/>
<br>


</br>

<button onClick={(e)=>handelAdd(e)}> Add</button>
</div>
</from>


      <section className='full'>
          <div className='children'>

        {data.filter(card=>card._id ===_id)
        .map((card,index)=>(

        <div key={index} className='fullCard'>

        <div className='fullImage'>
        <img src={card.image} alt='' width={300}/>

        </div>

    <div className='fullInfo'>
      <h1> Name of Author: <span>{card.name}.</span> </h1>             
                            <br/>
      <h1> Age of Author:<span>{card.age}.</span> </h1>
                            <br/>
     <h1> Nationality is:<span>{card.nationality}.</span> </h1>


        <hr></hr>
        
<div className='bookInfo'>
<h2>About the Book</h2>
        {card.books.map((item)=>{
            return  <div>
            <p><span>title of book:</span>{item.title}</p>
            <p><span>page of book:</span>{item.pages}</p>
            <p><span>price of book:</span>{item.price}</p>
            <img src={item.image} alt='' width={200}/>

        </div>
        })}
        </div>
     <div className="BTN">

        <button className='btnDE' onClick={()=>handelDelete(card._id)}>Delete</button> 
        {/* <button className='btnEDIT' onClick={()=>handelEdit(get._id)}>Edite</button>  */}
                
                </div>
                </div>

 </div >
            
        ))}

          </div>
      </section>
        </>
      );
}
